import React, { useState, useEffect } from 'react';
import ThemeToggle from './ThemeToggle';

const Navigation = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const smoothScroll = (e, targetId) => {
    e.preventDefault();
    setIsMobileMenuOpen(false);
    
    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      // Calculate position accounting for fixed navbar
      const navbarHeight = 80; // Approximate navbar height
      const targetPosition = targetElement.offsetTop - navbarHeight;
      
      window.scrollTo({
        top: targetPosition,
        behavior: 'smooth'
      });
    }
  };

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
      <div className="container">
        <div className="nav-content">
          <a href="#home" className="logo" onClick={(e) => smoothScroll(e, 'home')}>
            AK
          </a>
          
          <ul className={`nav-links ${isMobileMenuOpen ? 'active' : ''}`}>
            <li><a href="#about" onClick={(e) => smoothScroll(e, 'about')}>About</a></li>
            <li><a href="#projects" onClick={(e) => smoothScroll(e, 'projects')}>Projects</a></li>
            <li><a href="#experience" onClick={(e) => smoothScroll(e, 'experience')}>Experience</a></li>
            <li><a href="#certifications" onClick={(e) => smoothScroll(e, 'certifications')}>Certifications</a></li>
            <li><a href="#contact" onClick={(e) => smoothScroll(e, 'contact')}>Contact</a></li>
          </ul>
          
          <div className="nav-actions">
            <ThemeToggle />
            <button 
              className="mobile-menu-btn"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            >
              <i className={`fas ${isMobileMenuOpen ? 'fa-times' : 'fa-bars'}`}></i>
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
