import React, { useState, useEffect } from "react";
import "./App.css";

// Import components
import Navigation from "./components/Navigation";
import Header from "./components/Header";
import About from "./components/About";
import Projects from "./components/Projects";
import Experience from "./components/Experience";
import Certifications from "./components/Certifications";
import Contact from "./components/Contact";

function App() {
  const [showBackToTop, setShowBackToTop] = useState(false);

  // Handle scroll event for back to top button
  useEffect(() => {
    const handleScroll = () => {
      setShowBackToTop(window.pageYOffset > 300);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Scroll to top function
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth"
    });
  };

  // Smooth scroll function for navigation
  const smoothScroll = (e, targetId) => {
    e.preventDefault();
    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      targetElement.scrollIntoView({
        behavior: "smooth",
        block: "start"
      });
    }
  };

  return (
    <div className="App">
      <Navigation />
      <Header smoothScroll={smoothScroll} />
      <About />
      <Projects />
      <Experience />
      <Certifications />
      <Contact />
      
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-links">
              <a href="#about" onClick={(e) => smoothScroll(e, 'about')}>About</a>
              <a href="#projects" onClick={(e) => smoothScroll(e, 'projects')}>Projects</a>
              <a href="#contact" onClick={(e) => smoothScroll(e, 'contact')}>Contact</a>
            </div>
            <p className="copyright">
              &copy; {new Date().getFullYear()} Akshat Kashyap. All rights reserved.
            </p>
          </div>
        </div>
      </footer>
      
      <button 
        className={`back-to-top ${showBackToTop ? 'visible' : ''}`}
        onClick={scrollToTop}
        aria-label="Back to top"
      >
        <i className="fas fa-chevron-up"></i>
      </button>
    </div>
  );
}

export default App;
