import React, { useEffect, useRef } from 'react';

const Experience = () => {
  const experienceRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
        }
      },
      { threshold: 0.1 }
    );

    if (experienceRef.current) {
      observer.observe(experienceRef.current);
    }

    return () => {
      if (experienceRef.current) {
        observer.unobserve(experienceRef.current);
      }
    };
  }, []);

  const experiences = [
    {
      title: "Software Engineer Trainee",
      company: "Innodeed Systems",
      period: "Jun 2024 – Feb 2025",
      description: "Worked on developing and maintaining software solutions, collaborating with cross-functional teams to deliver high-quality products."
    },
    {
      title: "Data Engineer Intern",
      company: "Futurense Technologies",
      period: "Jan 2024 – May 2024",
      description: "Assisted in building data pipelines and ETL processes, gaining hands-on experience with big data technologies and cloud platforms."
    },
    {
      title: "Data Science Intern",
      company: "Suvidha Foundation",
      period: "Feb 2023 – Mar 2023",
      description: "Participated in data analysis projects, applied machine learning algorithms, and contributed to data-driven decision making processes."
    }
  ];

  return (
    <section className="section experience" id="experience" ref={experienceRef}>
      <div className="container">
        <h2 className="section-title fade-in">Professional Experience</h2>
        <div className="timeline">
          {experiences.map((exp, index) => (
            <div key={index} className="timeline-item fade-in">
              <div className="timeline-marker"></div>
              <div className="timeline-content">
                <div className="timeline-date">{exp.period}</div>
                <h3 className="timeline-title">{exp.title}</h3>
                <div className="timeline-company">{exp.company}</div>
                <p>{exp.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Experience;
